tests.code:
let query = [
  `UPDATE employees SET "email" = 'bilal.rafaqat@invozone.de' WHERE id = 9474;`,
  `UPDATE employees SET "email" = 'bilal.rafaqat@invozone.de' WHERE id = 9171;`,
  `UPDATE employees SET "email" = 'bilal.rafaqat@invozone.dev' WHERE id = 9429;`,
  `DELETE FROM "position_openings" WHERE "employeeId" = 9429;`,
];
await utilFun.udpateDatabase(query);
await positionOpenings.clickAddBtn();
await positionOpenings.selectField1AndEnterValue('Department*\nSelect Department', 'Engineering');
await positionOpenings.selectField1AndEnterValue('Name of Position*\nSelect Position', 'Software Engineer');
await positionOpenings.selectField2AndEnterValue('Position Level*\nSelect Level', 'Automation');
await positionOpenings.selectField2AndEnterValue('No. of Resources*', '3');
await positionOpenings.selectField2AndEnterValue(`Year's of Experience*\nSelect Years`, '0 - 1');
await positionOpenings.selectField1AndEnterValue('Tech Stack*\nSelect Tech Stack', 'Automation');
await positionOpenings.selectField1AndEnterValue('Minimum Education*\nSelect Education', 'Matric');
await positionOpenings.selectField1AndEnterValue('Gender*\nSelect Gender', 'Male');
await positionOpenings.selectField1AndEnterValue('Budget*\nSelect Budget', '0 - 25');
await positionOpenings.selectField1AndEnterValue('Office Location*\nSelect Location', 'Remote');
await positionOpenings.selectField1AndEnterValue('Priorty Level*\nSelect Priorty', 'High');
await positionOpenings.uploadDescription();
await positionOpenings.enterInstructions('Testing');
await positionOpenings.clickConfirmAddBtn();
const alertText = await utilFun.checkAlert();
assert.equal(alertText, 'The position request is created successfully!');

await positionOpenings.clickEditBtn();
await positionOpenings.enterInstructions(' Updated');
await positionOpenings.clickConfirmAddBtn();
const alertText = await utilFun.checkAlert();
assert.equal(alertText, 'The position opening request has been updated successfully');

await positionOpenings.clickDeleteBtn();
await positionOpenings.enterReason('testing');
await positionOpenings.clickYesBtn();
const alertText = await utilFun.checkAlert();
assert.equal(alertText, 'The position opening request has been deleted successfully!');

let query = [
  `UPDATE "position_openings" SET "deletedAt" = NULL WHERE "employeeId" = 9429;`,
  `UPDATE employees SET "email" = 'bilal.rafaqat@invozone.de' WHERE id = 9429;`,
  `UPDATE employees SET "email" = 'bilal.rafaqat@invozone.dev' WHERE id = 9474;`,
];
await utilFun.udpateDatabase(query);
let rowText = await positionOpenings.getPositionTblRow();
assert.ok(rowText.includes('Software Engineer'), `Expected text not found.`);

await positionOpenings.clickEditBtn();
await positionOpenings.enterInstructions(' Updated By HOD');
await positionOpenings.clickConfirmAddBtn();
const alertText = await utilFun.checkAlert();
assert.equal(alertText, 'The position opening request has been updated successfully');

await positionOpenings.clickRejectBtn();
await positionOpenings.enterReason('testing');
await positionOpenings.clickYesBtn();
const alertText = await utilFun.checkAlert();
assert.equal(alertText, 'The position opening request has been rejected successfully');

await positionOpenings.selectReqTblSubTab('Rejected');
let rowText = await positionOpenings.getPositionTblRow();
assert.ok(rowText.includes('Software Engineer'), `Expected text not found.`);

let query = [`UPDATE "position_openings" SET "isApprovedByHod" = 'PENDING' WHERE "employeeId" = 9429;`];
await utilFun.udpateDatabase(query);
await positionOpenings.clickApproveBtn();
await positionOpenings.enterReason('testing');
await positionOpenings.clickYesBtn();
const alertText = await utilFun.checkAlert();
assert.equal(alertText, 'The position opening request has been approved successfully');

await positionOpenings.selectReqTblSubTab('Approved');
let rowText = await positionOpenings.getPositionTblRow();
assert.ok(rowText.includes('Software Engineer'), `Expected text not found.`);

